function init(custom) {
    count1 = new CountUp('counter1', 0, custom.countup.attendees.number, 0, 3, special);
    count2 = new CountUp('counter2', 0, custom.countup.hours.number, 0, 3, options);
    count3 = new CountUp('counter3', 0, custom.countup.opportunity.number, 0, 3, options);
    var stuff = [{
        selector: '#counter1',
        offset: 0,
        callback: 'countUp()'
    }];
    Materialize.scrollFire(stuff);
    window.onblur = function () {
        document.title = custom.title
    }, window.onfocus = function () {
        document.title = custom.title
    };

    var rgb = custom.color.$value;

// navbar color
    $(window).scroll(function (event) {
        $('nav').removeClass('z-depth-0');

        var scroll = $(window).scrollTop();
        if (scroll <= 0)
            $('nav').addClass('z-depth-0');

        var scale = Math.max(0, Math.min(1, scroll / $(window).height()));
        $('nav').css('background-color', 'rgb(255, 255, 255, ' + scale + ')');
        $('.brand-logo').css('opacity', scale);

        $('.nav-text, .menu-btn .fa-bars').css('color', rgbToHex(255 - (255 - rgb[0]) * scale, 255 - (255 - rgb[1]) * scale, 255 - (255 - rgb[2]) * scale));
    });

// Button hover animations
    $('.header-button').hover(function () {
        $(this).css({
            backgroundColor: 'white',
            color: rgbToHex(rgb[0], rgb[1], rgb[2])
        });
    }, function () {
        $(this).css({
            backgroundColor: 'transparent',
            color: 'white'
        });
    });

    if (!$('#register').attr('href')) {
        $('#register').click(function () {
            Materialize.toast(custom.no_registration_yet, 2000);
        });
    }
}

!function (e) {
    e(function () {
        e('.button-collapse').sideNav({
            closeOnClick: !0
        }), e('.parallax').parallax(), e('body').scrollspy({}),
            e('nav ul a, #sponsor').click(function (event) {
                event.preventDefault();
                $section = $($(this).attr('href'));
                var scrollPos = $section.offset().top - ($section.hasClass('diagonal') ? 96 : 0);
                $('body,html').animate({
                    scrollTop: scrollPos
                }, 400);
            });
    })
}(jQuery);

var easingFn = function (e, n, o, t) {
        var a = (e /= t) * e,
            s = a * e;
        return n + o * (s + -3 * a + 3 * e)
    },
    options = {
        useEasing: !0,
        useGrouping: !1,
        separator: ',',
        decimal: '.',
        prefix: '',
        suffix: ''
    },
    special = {
        useEasing: !0,
        useGrouping: !1,
        separator: ',',
        decimal: '.',
        prefix: '',
        suffix: '+'
    };

var count1, count2, count3;

function countUp() {
    count1.start();
    count2.start();
    count3.start()
}

function componentToHex(c) {
    var hex = c.toString(16);
    return hex.length == 1 ? '0' + hex : hex;
}

function rgbToHex(r, g, b) {
    return '#' + componentToHex(Math.round(r)) + componentToHex(Math.round(g)) + componentToHex(Math.round(b));
}